// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TruffleFinance {
  IERC20 public toast;
  uint rewardsPercentPerDay = 1;

  struct Account {
    uint amount;
    uint updatedAt;
  }

  mapping(address => Account) public accounts;

  constructor(address _token) {
    toast = IERC20(_token);
  }

  function deposit(uint _amount) public {
    Account memory account = accounts[msg.sender];
    uint rewards = pendingRewards();
    account.amount += rewards; // update amount with pending rewards

    account.amount += _amount;
    account.updatedAt = block.timestamp;
    toast.transferFrom(msg.sender, address(this), _amount);
  }

  function withdraw(uint _amount) public {
    Account memory account = accounts[msg.sender];
    uint rewards = pendingRewards();
    account.amount += rewards; // update amount with pending rewards
    require(_amount <= account.amount, 'Amount error');

    account.amount -= _amount; // withdraw amount
    account.updatedAt = block.timestamp;
    toast.transfer(msg.sender, _amount);
  }

  function pendingRewards() public view returns(uint) {
    Account memory account = accounts[msg.sender];
    uint secondsElapsed = block.timestamp - account.updatedAt;
    uint rewardsPerSecond = (rewardsPercentPerDay * 1e16 /86400);
    return ((account.amount * rewardsPerSecond * secondsElapsed) / 1e16) / 100;
  }
}
